import React, { useState } from 'react';
import { View, 
         Text, 
         StyleSheet,
         Image,
         TextInput,
        TouchableOpacity, 
        SafeAreaView,
        Dimensions} from 'react-native';
import { Button, Avatar } from 'react-native-elements';
import {  AntDesign} from '@expo/vector-icons';
import FlatButton from '../components/FlatButton';
import HeaderComponent from '../components/HeaderComponent';
import ProfileScreen from '../screens/ProfileScreen';   
import Icon from 'react-native-vector-icons/FontAwesome';
import { Input } from 'react-native-elements';
import * as Animatable from 'react-native-animatable';





const LoginScreen = ({navigation}) => {



    const openMenu = () => {
        navigation.openDrawer(); 
    }



    return(


        <View>

                <HeaderComponent
                    openMenu={openMenu}
                />
        <View 
            style={styles.viewComponent}>


            <Icon

                    name='user-circle'
                    size={100}
                    color='black'
                    style={{marginVertical: 10}}
            />

            <Input //Input for Email
                placeholder='Enter Email Here'
                style={styles.email}
                inputContainerStyle={{borderBottomWidth:0}}
                placeholderTextColor='black'
                leftIcon={
                <Icon
                    name='user'
                    size={24}
                    color='black'
                />
                        }
            />


            <Input
                placeholder='Enter Password here'
                style={styles.email}
                secureTextEntry={true}
                inputContainerStyle={{borderBottomWidth:0}}
                placeholderTextColor='black'
                leftIcon={
                <Icon
                    name='lock'
                    size={24}
                    color='black'
                />
                    }
            />

            <TouchableOpacity 
            onPress={() => navigation.navigate('Home')}//Create Account Button
            style={{marginTop: 10}}
            >
            <View style = {{backgroundColor: '#f05454', 
                            alignItems: 'center', 
                            justifyContent: 'center',
                            borderWidth : 3, 
                            borderRadius: 100,
                            width: (Dimensions.get('window').width)*(0.55),
                            height: 50}}
            >
                <Text style = {{color: 'black',
                                fontWeight: 'bold',
                                fontSize: 20}}>Log-In</Text>
            </View>
        </TouchableOpacity>
        </View>
            </View>          

    )
}

const styles = StyleSheet.create({
    mainComponent : {
        marginTop : 100
    },
    viewComponent : {
        paddingTop : 100,
        marginTop : 100
    },
    bloodBankLogo : {
        width : 50,
        height : 100,
        resizeMode: 'stretch'
   } , 
   email : {
       marginHorizontal : (Dimensions.get('window').width)*(0.001),
       borderWidth : 2,
       borderRadius : 100,    
       backgroundColor: '#fff3e2',
       textAlign: 'center',
   },
   viewComponent : {
    alignItems : 'center'   
   }
});

export default LoginScreen;
