import React from 'react';
import { View,
         Text, 
         TouchableOpacity,
         StyleSheet,
         Dimensions,
         ScrollView } from 'react-native';
import { Input } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';
import HeaderComponent from '../components/HeaderComponent';
import {Formik} from 'formik';
import NicValidation from '../jscodes/NicValidation';
import * as yup from 'yup';



const ReviewSchema = yup.object({
    name: yup.string()//must be string
        .required(),//need to fill the input

    nic: yup.string()//should be string
        .required()//should fill
        .test('isValidNic','Invalid NIC number',(val) => {//val is the value of the nic
           return NicValidation(val) == true;
        }), 

    address: yup.string()//should be string
             .required(),//field is required

    mail: yup.string()
          .required()
          .email(),//email validation
    
    number: yup.string()//phone number validation
            .required()
            .min(10),

    password: yup.string()//pasword validation
              .required('No Password provided')
              .min(8),

    passwordAgain: yup.string()//password confirmation
                   .required('Enter your password again')
                   .oneOf([yup.ref('pasword'), null],'Password must match')
}); 

const UserRegisterScreen = ({navigation}) => {

    const openMenu = () => {
        navigation.openDrawer(); 
    }
   
    return(
        
        <ScrollView>
         
                <HeaderComponent
                    openMenu={openMenu}
                />

            
               
                <Text style={styles.signIn}>Sign In</Text>
          

            <Formik
                initialValues={{ name: '',
                                 nic: '',
                                 address: '',
                                 mail: '',
                                 number: '',
                                 password: '',
                                 passwordAgain: ''
                                }}
                validationSchema={ReviewSchema}
                onSubmit={(values) => {
                    console.log(values);
                }}
                >
                {(props) => (
                    <View style={styles.form}>

                    <Text style={styles.errorMessage}>{props.touched.name && props.errors.name}</Text>
                        <Input //Enter Name number here
                            placeholder='Enter Name here'
                            style={styles.name}
                            onChangeText={props.handleChange('name')}
                            value={props.values.name}
                            inputContainerStyle={{borderBottomWidth:0}}
                            placeholderTextColor='black'
                            multiline={true}
                            onBlur={props.handleBlur('name')}
                            leftIcon={
                                <Icon
                                  name='user'
                                  size={24}
                                  color='black'
                                />
                              }
                        />

                        <Text style={styles.errorMessage}>{props.touched.nic && props.errors.nic}</Text>
                        <Input //Enter NIC here
                            placeholder='Enter national ID number here'
                            style={styles.name}
                            onChangeText={props.handleChange('nic')}
                            value={props.values.nic}
                            inputContainerStyle={{borderBottomWidth:0}}
                            placeholderTextColor='black'
                            onBlur={props.handleBlur('nic')}
                            leftIcon={
                                <Icon
                                  name='id-card'
                                  size={24}
                                  color='black'
                                />
                              }
                        />

                        
                        <Text style={styles.errorMessage}>{props.touched.address && props.errors.address}</Text>
                        <Input //Enter Adress here
                            placeholder='Enter Adress here'
                            style={styles.name}
                            onChangeText={props.handleChange('address')}
                            value={props.values.address}
                            inputContainerStyle={{borderBottomWidth:0}}
                            placeholderTextColor='black'
                            multiline={true}
                            onBlur={props.handleBlur('address')}
                            leftIcon={
                                <Icon
                                  name='map-marker'
                                  size={24}
                                  color='black'
                                />
                              }
                        />
                        

                        <Text style={styles.errorMessage}>{props.touched.mail &&props.errors.mail}</Text>
                        <Input //Enter Email here
                            placeholder='Enter Email here'
                            style={styles.name}
                            onChangeText={props.handleChange('mail')}
                            value={props.values.mail}
                            inputContainerStyle={{borderBottomWidth:0}}
                            placeholderTextColor='black'
                            onBlur={props.handleBlur('mail')}
                            leftIcon={
                                <Icon
                                  name='envelope'
                                  size={24}
                                  color='black'
                                />
                              }
                        />
                        

                        <Text style={styles.errorMessage}>{props.touched.number && props.errors.number}</Text>
                        <Input //Enter Contact number here
                            placeholder='Enter Contact number here'
                            style={styles.name}
                            onChangeText={props.handleChange('number')}
                            value={props.values.number}
                            inputContainerStyle={{borderBottomWidth:0}}
                            placeholderTextColor='black'
                            keyboardType='numeric'
                            onBlur={props.handleBlur('number')}
                            leftIcon={
                                <Icon
                                  name='phone'
                                  size={24}
                                  color='black'
                                />
                              }
                        />

                        <Text style={styles.errorMessage}>{props.touched.password && props.errors.password}</Text>
                        <Input //Enter password here
                            placeholder='  Enter password here'
                            style={styles.name}
                            onChangeText={props.handleChange('password')}
                            value={props.values.password}
                            inputContainerStyle={{borderBottomWidth:0}}
                            placeholderTextColor='black'
                            secureTextEntry={true}
                            onBlur={props.handleBlur('password')}
                            leftIcon={
                                <Icon
                                  name='key'
                                  size={24}
                                  color='black'
                                />
                              }
                        />
 
                        <Text style={styles.errorMessage}>{props.touched.passwordAgain && props.errors.passwordAgain}</Text>
                        <Input //reEnter password here
                            placeholder='Enter password again here'
                            style={styles.name}
                            onChangeText={props.handleChange('passwordAgain')}
                            value={props.values.passwordAgain}
                            inputContainerStyle={{borderBottomWidth:0}}
                            placeholderTextColor='black'
                            secureTextEntry={true}
                            onBlur={props.handleBlur('passwordAgain')}
                            leftIcon={
                                <Icon
                                  name='key'
                                  size={24}
                                  color='black'
                                />
                              }
                        />
                        
                        <TouchableOpacity 
                            onPress={() => {}}//Create Account Button
                            disabled={!(Formik.isValid && Formik.dirty)}
                        >
                            <View style = {{backgroundColor: '#16a596', 
                                            alignItems: 'center', 
                                            justifyContent: 'center', 
                                            borderRadius: 100,
                                            width: (Dimensions.get('window').width)*(0.5),
                                            height: 50}}
                            >
                                <Text style = {{color: 'black',
                                                fontWeight: 'bold',
                                                fontSize: 20}}>Create Account</Text>
                            </View>
                        </TouchableOpacity>
                    
                    </View>
                )}
            </Formik>
        </ScrollView>
    
    
    );
};

const styles = StyleSheet.create({
    name : {
        borderWidth : 2,
        borderRadius : 100,    
        backgroundColor: '#fff3e2',
        textAlign: 'center',
    },
    form : {
        marginTop : 30,
        alignItems: 'center', 
        justifyContent: 'center',
        marginBottom: 50
    },
    textInput : {
        textAlign : 'center',
        height : 40,
    },
    nameIcon: {
        marginTop: 5,
    },
    signIn: {
        fontWeight: 'bold',
        fontSize: 40,
        textAlign: 'center',
        marginTop: 30,
        textShadowColor: 'rgba(0, 0, 0, 0.75)',
        textShadowOffset: {width: -1, height: 1},
        textShadowRadius: 5
    },
    errorMessage:{
        fontSize: 10
    }
  
})

export default UserRegisterScreen;
