import React from 'react';
import { View, 
         Text, 
         StyleSheet,
         Image,
         Dimensions,
         StatusBar } from 'react-native';
import {  Entypo} from '@expo/vector-icons';


const HeaderComponent = () => {
    
    return(
       <View style={styles.header}>
            
            <Image
                source={require('../../assets/web1_blood1.jpg')}
                style={styles.bloodDrop}
            />
            <View>
                <Text style={styles.bloodBankHeading}>
                    Blood Bank  
                </Text>

                <Text style={styles.bloodBanHeading2}>
                    Donate Blood Save Lives
                </Text>
            </View>
            <StatusBar style="auto" />
       </View> 
    ); 
};


  
const styles = StyleSheet.create({
    header: {
        flexDirection: 'row',
        justifyContent: 'flex-start',
        backgroundColor: '#E64C4C',
        width: Dimensions.get('window').width
    }, 
    bloodDrop: {
        width: (Dimensions.get('window').width)*(0.2777),
        height: (Dimensions.get('window').width)*(0.2777),
        resizeMode: 'stretch',
        marginLeft: (Dimensions.get('window').width)*(0.0277), // get the dimensions
        marginBottom: (Dimensions.get('window').width)*(0.0277),
        marginRight: (Dimensions.get('window').width)*(0.0277),
        marginTop: (Dimensions.get('window').width)*(0.0277),
    },
    bloodBankHeading: {
        fontSize: 35,
        fontWeight: "bold",
        borderBottomWidth: 2,
        letterSpacing: 1
    },
    icon: {
        marginTop: 30
    },
    bloodBanHeading2: {
        letterSpacing: 2
    }
});

export default HeaderComponent;
