//React Essentials
import * as React from 'react';
import { useWindowDimensions, 
         ActivityIndicator,
         View } from 'react-native';

//Navigators 
import { createDrawerNavigator } from '@react-navigation/drawer';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { DrawerNavigator } from 'react-navigation';

//Import Screens
import  HomeScreen  from './src/screens/HomeScreen';
import  LoginScreen  from './src/screens/LoginScreen';
import  ProfileScreen from './src/screens/ProfileScreen';
import  ViewBloodCamps from './src/screens/ViewBloodCamps';
import  DonorFormScreen from './src/screens/DonorFormScreen';
import  UserRegisterScreen  from './src/screens/UserRegisterScreen';
import  LogOutScreen from './src/screens/LogOutScreen';
import  SettingsScreen from './src/screens/SettingsScreen';
import  InitialScreen from './src/screens/InitialScreen';
import  BloodDonationScreen from './src/screens/BloodDonationScreen';
import  AppoinmentScreen from './src/screens/AppoinmentScreen';  
import SuitbleDateScreen from './src/screens/SuitbleDateScreen';
import EmailVerificationScreen from './src/screens/EmailVerificationScreen'

//Vector Icons
import Icon from 'react-native-vector-icons/FontAwesome';




const Drawer = createDrawerNavigator();
const Stack = createStackNavigator();

const mainDrawerNavigator = () => {//component for drawer navigator
  const dimensions = useWindowDimensions();
  return (    

      <Drawer.Navigator> 

        <Drawer.Screen 
          name="Home" 
          component={HomeScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="home" 
                style={{fontSize: size, color: color}}/>
            ),
          }}
        />

        <Drawer.Screen 
          name="Blood Donation Camps" 
          component={ViewBloodCamps}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon  
                name="home" 
                style={{fontSize: size, color: color}}/>
            ),
          }}
        />


        <Drawer.Screen       
          name="My Profile" 
          component={ProfileScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="home" 
                style={{fontSize: size, color: color}}/>
            ),
          }}
        />
        <Drawer.Screen 
          name="Log out" 
          component={LogOutScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="sign-out"  
                style={{fontSize: size, color: color}}/>
            ),
          }}      
        />
        <Drawer.Screen 
          name="Make an Appoinment" 
          component={AppoinmentScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="sign-out"  
                style={{fontSize: size, color: color}}/>
            ),
          }}      
        />
        <Drawer.Screen 
          name="About Blood Donation" 
          component={BloodDonationScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="sign-out"  
                style={{fontSize: size, color: color}}/>
            ),
          }}      
        />
        <Drawer.Screen 
          name="Donor Form" 
          component={DonorFormScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="sign-out"  
                style={{fontSize: size, color: color}}/>
            ),
          }}      
        />
        <Drawer.Screen 
          name="Settings" 
          component={SettingsScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="cogs"  
                style={{fontSize: size, color: color}}/>
            ),
          }} 
        />


        <Drawer.Screen 
          name="Suitable Date" 
          component={SuitbleDateScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="cogs"  
                style={{fontSize: size, color: color}}/>
            ),
          }} 
        />

        <Drawer.Screen 
          name="Email Verification" 
          component={EmailVerificationScreen}
          options={{ //add an icon to home
            headerLeft:null,
            drawerIcon: ({color,size}) => (
              <Icon 
                name="cogs"  
                style={{fontSize: size, color: color}}/>
            ),
          }} 
        />

      </Drawer.Navigator>

  );
}

const App = () => {
  const [isLoading, setisLoading] = React.useState(false);
  const [userToken, setuserToken] = React.useState(null);

  if(isLoading){
    return(
      <View style={{flex:1,justifyContent: 'center',alignItems: 'center'}}>
        <ActivityIndicator size='small'/>
      </View>
    );
  }
  return(
    <NavigationContainer>
      <Stack.Navigator initialRouteName="WelCome">
        <Stack.Screen name="WelCome" //Initial Screen
          component={InitialScreen}
        />
        <Stack.Screen name="Sign-In" //UserRegister Screen
          component={UserRegisterScreen} 
        />
        <Stack.Screen name="Log-In" //log-In Screen
          component={LoginScreen} 
        />
        <Stack.Screen 
          name="Home" //Main HOme Screen 
          component={mainDrawerNavigator} 
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;
