import React from 'react';
import {View,
        Text,
        Image,
        StyleSheet,
        } from 'react-native';
import { Button,
         Input } from 'react-native-elements';
import { ScrollView } from 'react-native-gesture-handler';
import Icon from 'react-native-vector-icons/FontAwesome';

const EmailVerificationScreen = () => {
    return(
    <ScrollView>
        <View style={styles.mainComponent}>
            <Image
                source={require('../../assets/Confirmed-bro.png')}
                style={styles.mainImage}
            />

                <View style={styles.mailAgain}>
                    <Text style={{marginBottom: 10}}>Didn't get code yet?chech your mails</Text>
                    <Button
                        title="Send verification code again"
                    />
                    <Text style={{marginTop: 40}}>
                        enter the verification code here!
                    </Text>
                   
                </View>

                <Input
                    placeholder='Enter the code here'
                    leftIcon={
                        <Icon
                          name='lock'
                          size={24}
                          color='black'
                        />
                      }
                />

                <Button
                    title="Create an account"
                />
        </View>
    </ScrollView>
    );
};

const styles = StyleSheet.create({
    mainImage: {
        width : 200,
        height: 200,
        resizeMode: 'stretch'
    },
    mainComponent: {
        alignItems: 'center',
        marginBottom: 100
    },
    mailAgain: {
        marginTop: 50
    }
});
export default EmailVerificationScreen;
