import React, { useState } from 'react';
import { View, 
         Text, 
         StyleSheet,
         Image,
         TextInput,
        TouchableOpacity, 
        SafeAreaView,
        Dimensions} from 'react-native';
import { Button, Avatar } from 'react-native-elements';
import {  AntDesign} from '@expo/vector-icons';
import FlatButton from '../components/FlatButton';
import HeaderComponent from '../components/HeaderComponent';
import ProfileScreen from '../screens/ProfileScreen';   
import Icon from 'react-native-vector-icons/FontAwesome';
import { Input } from 'react-native-elements';





const LoginScreen = ({navigation}) => {



    const openMenu = () => {
        navigation.openDrawer(); 
    }



    return(


        <View>
                <HeaderComponent
                    openMenu={openMenu}
                />
        <View 
            style={styles.viewComponent}>



            <Avatar
                size="large"
                rounded
                source={require('../../assets/web1_blood1.jpg')}
            />



            <Input //Input for Email
                placeholder='Email'
                style={styles.email}
                leftIcon={
                <Icon
                    name='user'
                    size={24}
                    color='black'
                />
                        }
            />

            <Input
                placeholder='Password'
                style={styles.email}
                leftIcon={
                <Icon
                    name='lock'
                    size={24}
                    color='black'
                />
                    }
            />

            <Button
                icon={
                    <Icon
                        name="sign-in"
                        size={15}
                        color="white"
                    />
                    }
                title="Login"
            />
        </View>
            </View>          

    )
}

const styles = StyleSheet.create({
    mainComponent : {
        marginTop : 100
    },
    viewComponent : {
        paddingTop : 100,
        marginTop : 100
    },
    bloodBankLogo : {
        width : 50,
        height : 100,
        resizeMode: 'stretch'
   } , 
   email : {
       marginHorizontal : (Dimensions.get('window').width)*(0.3)
   },
   viewComponent : {
    alignItems : 'center'   
   }
});

export default LoginScreen;
