import React,{useState, useEffect} from 'react';
import { View,
         Text, 
         TouchableOpacity,
         StyleSheet,
         Dimensions,
         ScrollView } from 'react-native';
import MapView, {Marker, PROVIDER_GOOGLE,OverlayComponent}  from 'react-native-maps';
import * as Location from 'expo-location';

const ViewBloodCamps = () => {
    
        

        const [latitude, setlatitude] = useState(7.0231);
        const [longitude, setlongitute] = useState(79.9272);
        const [latitudeDelta, setlatitudeDelta] = useState(0.0922);
        const [longitudeDelta ,setlongitudeDelta] = useState(0.0421);

     

        const [location, setlocation] = useState([
            {
                latitude: 7.0234,
                longitude: 79.9272,
                title: 'Ape Gedara',
                description: 'Blood Donation Camp 1',
                key: 12
            },
            {
                latitude: 7.0229,
                longitude: 79.9270,
                title: 'University fo Colombo',
                desription: 'Blood Donation camp 2',
                key: 13
            }
        ]);


        


    return(
        <View>
            <MapView
                provider={PROVIDER_GOOGLE}
                initialRegion={{
                    latitude,
                    longitude, 
                    latitudeDelta,
                    longitudeDelta,
                                }}
                style={styles.map}
                
            >              
            {
                location.map(marker => (
                    <Marker
                        coordinate={{
                            latitude: marker.latitude,
                            longitude: marker.longitude
                        }}
                        title={marker.title}
                        description={marker.description}
                        key={marker.key}
                    />
                ))
            }
            </MapView>
           
        </View> 
    );
}; 

const styles = StyleSheet.create({
    map: {
        height: '90%',
        width: '99%'
    }
});

export default ViewBloodCamps;
