import React,{useState} from 'react';
import { Button, 
         View, 
         Text, 
         StyleSheet,
         ScrollView } from 'react-native';
import { MaterialIcons, AntDesign} from '@expo/vector-icons';
import HeaderComponent from '../components/HeaderComponent';
import { Table, Row, Rows } from 'react-native-table-component';


const ProfileScreen = ({navigation}) => {
    const openMenu = () => { // function for open and close the burger menu
        navigation.openDrawer();
    }

    const [tableHead, settableHead] = useState(['ID','DATA','PLACE']);
    const [tableData, settableData] = useState([['234AB1', '2019/10/14', 'University of Ruhuna'],
                                                ['43BG1S', '2020/01/15', 'University of Colombo'],
                                                ['2323GH', '2020/05/13', 'University of Ruhuna'],
                                               ]);
    
            
   return (
    <ScrollView>
       <View style={{backgroundColor: 'white'}}>
            <HeaderComponent//component for burger menu
            openMenu={openMenu}/>

           
                <Text style={styles.Heading}> My Profile </Text>

                    
                        <View style={styles.informationComponent}>
                            <View style={styles.name}//name field
                            >
                                <Text style={styles.lable}>Name  :-  </Text>
                                <Text style={{fontSize: 20}}>S.D.N Silva</Text>
                            </View>

                                <View style={styles.address}//Address field
                                >
                                    <Text style={styles.lable}>Address  :-  </Text>
                                    <Text style={{fontSize: 20}}>850/A Kadawatha Ragama</Text>
                                </View>

                                    <View style={styles.address}//Nic field
                                    >
                                        <Text style={styles.lable}>NIC Number  :-  </Text>
                                        <Text style={{fontSize: 20}}>963002157v</Text>
                                    </View>

                                        <View style={styles.address}//Birthday field
                                        >
                                            <Text style={styles.lable}>Birthday  :-  </Text>
                                            <Text style={{fontSize: 20}}>1996/10/26</Text>
                                        </View>

                                            <View style={styles.address}//Age field
                                            >
                                                <Text style={styles.lable}>Age  :-  </Text>
                                                <Text style={{fontSize: 20}}>24</Text>
                                            </View>

                                                <View style={styles.address}//Last Donated date field
                                                >
                                                    <Text style={styles.lable}>Last Donated Date  :-  </Text>
                                                    <Text style={{fontSize: 20}}>None</Text>
                                                </View>

                                                <View style={styles.address}//Last Donated date field
                                                >
                                                    <Text style={styles.lable}>Next Donate Day :- </Text>
                                                    <Text style={{fontSize: 20,
                                                                 color : 'red',
                                                                 fontWeight: 'bold',}}>2020/12/12</Text>
                                                </View>

                                            <View //horizontal line
                                                style={{
                                                  borderBottomColor: 'black',
                                                  borderBottomWidth: 3,
                                                  marginTop: 50
                                                }}
                                              />
                            
                                        <Text style={styles.Heading}//Donor history Heading
                                        > 
                                            Donor History 
                                        </Text>

                                    
                                
                                      <View style={styles.container}//blood donation history table
                                      >
              
                                                  <Table 
                                                    borderStyle={{borderWidth: 2, 
                                                                borderColor: '#c8e1ff'}}
                                                  >
                                                      <Row 
                                                        data={tableHead} 
                                                        style={styles.head} 
                                                        textStyle={styles.text}/>
                                                      <Rows 
                                                        data={tableData} 
                                                        textStyle={styles.text}/>
                                                  </Table>
              
                                      </View>
                                
                                      <View //horizontal line
                                        style={{
                                          borderBottomColor: 'black',
                                          borderBottomWidth: 3,
                                          marginTop: 50,
                                          marginBottom: 50
                                        }}
                                      />
                                
                                
                        </View>
                          
                        

           
       </View>
       
    </ScrollView>
   );
};

const styles = StyleSheet.create({
    Heading: {
        fontWeight: 'bold',
        fontSize: 40,
        textAlign: 'center',
        marginTop: 30,
        textShadowColor: 'rgba(0, 0, 0, 0.75)',
        textShadowRadius: 5,
        textDecorationLine: 'underline',
        textShadowOffset: {width: -1, height: 1}
    },
    lable: {
        fontWeight: 'bold',
        fontSize: 20,
        textShadowRadius: 5,
    },
    informations: {
        marginLeft: 10
    },
    name: {
        flexDirection: 'row', 
        marginTop: 45,
    },
    informationComponent: {
        marginTop: 10,
        marginLeft: 10,
        marginRight: 10
    },
    address: {
        marginTop: 10,
        flexDirection: 'row',
    },
    container: { 
        flex: 1, 
        padding: 16, 
        paddingTop: 30, 
        backgroundColor: '#fff'
    },
    head: { 
        height: 40, 
        backgroundColor: '#f1f8ff' 
    },
    text: { 
        margin: 6 
    }
});

export default ProfileScreen;
