import React,{useState} from 'react';
import {View,
        Image,
        StyleSheet,
        Text,
        Platform,
        Button,
        TouchableOpacity,
        Dimensions,
        ScrollView} from 'react-native';
import { Input } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';
import * as Animatable from 'react-native-animatable';
import DateTimePicker from '@react-native-community/datetimepicker';

const App = () => {
    const [date, setDate] = useState(new Date());
    const [mode, setMode] = useState('date');
    const [show, setShow] = useState(false);
  
    const onChange = (event, selectedDate) => {
      const currentDate = selectedDate || date;
      setShow(Platform.OS === 'ios');
      setDate(currentDate);
    };
  
    const showMode = (currentMode) => {
      setShow(true);
      setMode(currentMode);
    };
  
    const showDatepicker = () => {
      showMode('date');
    };
  
    const showTimepicker = () => {
      showMode('time');
    };
  
    return (
      <View>
        <View>
            <UserInput
                Press={showDatepicker} title="Pick Your Date"
            />

            <UserInput
                Press={showTimepicker} 
                title="Pick Your Time"
           />
        </View>

        

        {show && (
          <DateTimePicker
            testID="dateTimePicker"
            value={date}
            mode={mode}
            is24Hour={true}
            display="default"
            onChange={onChange}
          />
        )}
      </View>
    );
  };

const UserInput = (props) => {
    return (
        <View  style={{
            alignItems: 'flex-start',
            backgroundColor: 'white',
            height: (Dimensions.get('window').height)*(0.1),
            
        }}>

        <Animatable.View 
            animation="bounceIn"
            duration={2000}
            
        >

            <TouchableOpacity //Button for Sign-In button
                onPress={props.Press}
                style={{alignItems: 'center',
                        justifyContent: 'center'}}
            >

                <View style = {styles.viewComponent}>
                    <Text style = {styles.textComponent}>{props.title}</Text>
                </View>
            </TouchableOpacity>

</Animatable.View>
</View>
    );
}

const SuibleDateScreen = (props) => {

    return(
        <ScrollView>
            <View //Main Component
                style={styles.mainContent}>
                    <Text style={styles.mainText}>Fill Your Information Here</Text>
                <Image
                    source={require('../../assets/Order.png')}
                    style={styles.mainImage}
                />
                
            </View>

            <App/>
        </ScrollView>
    );
}

const styles = StyleSheet.create({
    mainImage: {
        width: 200,
        height: 200,
        resizeMode: 'stretch',
        marginTop: 10
        
    },
    mainContent: {
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'white'
    },
    mainText: {
        marginTop: 20
    },
    date: {
        marginLeft: 10,
        borderWidth: 1,
        borderRadius: 100,
        textAlign: 'center' 
    },
    text: {
        marginLeft: 60,
        color : '#7e7474'
    },
    viewComponent: {
        backgroundColor: '#16a596', 
        alignItems: 'center', 
        justifyContent: 'center', 
        borderRadius: 100,
        width: (Dimensions.get('window').width)*(0.5),
        height: 50,
        borderWidth : 3, 
    },
    textComponent: {
        color: 'black',
        fontWeight: 'bold',
        fontSize: 20
    }
});
export default SuibleDateScreen;
